// Environment
import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

// Components

  
  
  

  
  
  
  
const HardSkills = () => {

  const elementsRef = useRef([]);

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    elementsRef.current.forEach((element) => {
      gsap.fromTo(
        element,
        { opacity: 0 },
        {
          opacity: 1,
          duration: 0.5,
          scrollTrigger: {
            trigger: element,
            start: 'center 85%',
            end: 'center 15%',
            toggleActions: 'play reverse play reverse',
            markers: true
          },
        }
      );
    });
  }, []);



    return (
      <div id="hard-skills">

        <div className="skills-title-container">
          <p className="skills-title">
            Hard Skills
          </p>
        </div>

        <div className="skill-master-container"> 
          <div className="skill-container-1">
            <p className="skill-text">
              Frontend Hard Skills
            </p>
            <ul class="skills-list">
              <li >HTML</li>
              <li >CSS</li>
              <li >Javascript</li>
              <li >React</li>
              <li >React Native</li>
              <li >Typescript</li>
              <li >Redux</li>
              <li >Git</li>
              <li >SASS</li>
              <li >Lottie</li>
              <li >Bootstrap</li>
              <li >GitHub</li>
            </ul>
          </div>
          <div className="skill-container-2">
            <p className="skill-text">
              3D/2D Motion Design Skills
            </p>
            <ul class="skills-list">
              <li >Cinema 4D</li>
              <li >Houdini</li>
              <li >Redshift Engine</li>
              <li >Substance Painter</li>
              <li >Substance Designer</li>
              <li >Marvelous Designer</li>
              <li >After Effects</li>
              <li >Illustrator</li>
              <li >Photoshop</li>
              <li >Premiere</li>
            </ul>
          </div>
        </div>
  
      </div>
    );
  }
  
  export default HardSkills;
