// Environment
import { useRef } from "react";
import { useIntersection } from "react-use";
import gsap from "gsap";

// Components

  
  
  

  
  
  
  
const HardSkills = () => {


  const ratio = 1;
  // Ref for our element
const ResumeRef = useRef(null);
// All the ref to be observed
const intersection = useIntersection(ResumeRef, {
root:null,
rootMargin: "0px",
threshold: ratio
});

// Animation for fading in
const fadeIn = element => {
gsap.to(element, 1, {
  opacity: 1,
  y: 0,
  ease:"power4.out",
  stagger:{
    amount: 0.5
  }
});
};

// Animation for fading out
const fadeOut = element => {
gsap.to(element, 1, {
  opacity: 0,
  y: 20,
  ease: "power4.out"
})
};

// Checking to see when the viewport is visible to the user
intersection && intersection.intersectionRatio < ratio
? fadeOut(".fadeIn")
: fadeIn(".fadeIn");




    return (
      <div id="hard-skills">

        <div className="skills-title-container">
          <p className="skills-title">
            Hard Skills
          </p>
        </div>

        <div className="skill-master-container"> 
          <div class="skill-container-1">
            <p className="skill-text">
              Frontend Hard Skills
            </p>
            <ul class="skills-list">
              <li>HTML</li>
              <li>CSS</li>
              <li>Javascript</li>
              <li>React</li>
              <li>React Native</li>
              <li>Typescript</li>
              <li>Redux</li>
              <li>SASS</li>
              <li>Lottie</li>
              <li>Bootstrap</li>
              <li>Git</li>
              <li>GitHub</li>
            </ul>
          </div>
          <div class="skill-container-2">
            <p className="skill-text">
              3D/2D Motion Design Skills
            </p>
            <ul class="skills-list">
              <li>Cinema 4D</li>
              <li>Houdini</li>
              <li>Redshift Engine</li>
              <li>Substance Painter</li>
              <li>Substance Designer</li>
              <li>Marvelous Designer</li>
              <li>After Effects</li>
              <li>Illustrator</li>
              <li>Photoshop</li>
              <li>Premiere</li>
            </ul>
          </div>
        </div>
  
      </div>
    );
  }
  
  export default HardSkills;
